type Translations = typeof t;

const proxyHandler = {
  get(target: any, lang: LanguageCode) {
    // This recursive function translates an object structure based on the selected language.
    const translate = (obj: any): any => {
      // Base cases: if it's not an object, or is null, return it as is.
      if (typeof obj !== 'object' || obj === null) {
        return obj;
      }
      
      // If it's an array, translate each item inside it.
      if (Array.isArray(obj)) {
        return obj.map(item => translate(item));
      }
      
      // Heuristic for identifying a "leaf" translation node.
      // We assume that if an object has an 'en' key, it's a map of translations.
      if (obj.hasOwnProperty('en')) {
        // Return the translation for the current language, or fallback to English.
        return obj[lang] ?? obj['en'];
      }

      // If it's a regular object, recurse through its properties.
      const translatedObj: { [key: string]: any } = {};
      for (const key in obj) {
        if (Object.prototype.hasOwnProperty.call(obj, key)) {
          translatedObj[key] = translate(obj[key]);
        }
      }
      return translatedObj;
    };
    
    // Start the translation process from the target object (e.g., t.shopPage).
    return translate(target);
  }
};


// The types have been updated to be recursive, accurately modeling the shape of
// the translated object. This improves type safety and autocompletion.
type Translated<T> =
  T extends (...args: any[]) => any ? T : // Keep functions as they are
  T extends Record<LanguageCode, infer V> ? V : // If it's a map of lang codes to values, extract the value
  T extends object ? { [P in keyof T]: Translated<T[P]> } : // If it's an object, recurse
  T; // Primitives

type FinalTranslations = {
  [K in keyof Translations]: Record<LanguageCode, Translated<Translations[K]>>;
};


const translationsProxy = new Proxy(t, {
    get(target: Translations, prop: keyof Translations) {
        return new Proxy(target[prop], proxyHandler);
    }
});

const translations = translationsProxy as unknown as FinalTranslations;

export const rawTranslations = t;

export default translations;